css:
.top-bar {
background-color: #e8e1d5;
}
.header {
background-color: #e8e1d5;
}
.footer 
{
background-color: #e8e1d5;
}
body, p, .btn {
  font-family: 'Poppins', sans-serif !important;
}
.product__short-stock .fas.fa-forward, .product__short-stock strong, .product__short-stock strong:before, .product__short-stock strong:after {
font-size: .875rem !important
}
.product__short-stock strong:before {
content: 'Apenas '
}
.product__short-stock strong:after {
content: ' em estoque'
}
.product__short-stock {
font-size: 0px
}
.product__discount {
display: none !important;
}
.product-card__buy.fade {
  opacity: 1;
}
.product-card--small .product-card__buy {
  position: static;
  display: block;
}
.banner {
    width: 100% !important;
}
.shipping-calculator__services::before {
  content: "⚠️ GREVE DOS CORREIOS E COVID podem ocasionar atraso no recebimento do seu produto.";
  border: 1px solid #000;
  border-radius: 2rem;
  padding: 1.5rem;
  font-size: 1.2rem;
  display: inherit;
  margin-bottom: 1rem
}
